mapper.select:

    select *
	  from (
	  		select rownum num, A.*
	  		  from (
	  		        select *
	  		          from c_board

						  where ${searchField} like '%${searchWord}%'

	  		         order by c_num desc
	  		          ) A
	  		  where rownum <= #{param1} ) B
	  where B.num >= #{param2}

SELECT EXISTS (
  SELECT 1 FROM c_board_like
  WHERE c_num = #{c_num} AND u_id = #{u_id}
)
