mapper.update:
update c_reply set
	u_id = #{u_id, jdbcType=VARCHAR},
	c_content = #{c_content}
where c_rno = #{c_rno}
	and c_num = #{c_num}

mapper.delete:
delete from c_reply
where c_rno = #{c_rno}
	and c_num = ${c_num}

mapper.select:
select
	c_rno, c_num, u_id, c_content, c_regDate
from c_reply
	where c_num = #{c_num}
	and c_rno = #{c_rno}
