mapper.select:
select r_num from p_review
 where p_num = #{param1}
   and u_id = #{param2}

select * from p_review 
where p_num = #{param1}
order by r_good desc

select u_id
  from p_revlike
 where r_num = #{param1}
   and u_id = #{param2}

mapper.update:
update p_review
   set r_good = r_good + 1
 where r_num = #{param1}

UPDATE product_list p
SET p.p_rating = (
  SELECT SUM(r.r_rating)/COUNT(*) AS avg_rating
  FROM p_review r
  WHERE r.p_num = p.p_num
    AND p_num = #{param1}
)
where p_num = #{param1}

mapper.delete:
delete from p_review
 where r_num = #{param1}
   and p_num = #{param2}

delete from p_revimage
 where r_num = #{param1}
   and p_num = #{param2}

mapper.select:
select r_sfile
  from p_revimage
 where r_num = #{param1}
   and p_num = #{param2}
