mapper.select:
		select count(*) as total from product_list

		 	where ${param2} = #{param3}


          	where upper(${param2}) like '%${param3.toUpperCase()}%'

select *
  from (
  		select rownum num, A.*
  		  from (
  		        select *
  		          from product_list

  		           	where ${param3} = #{param4}


  		          	where upper(${param3}) like '%${param4.toUpperCase()}%'

  		         ) A
  		  where rownum <= #{param1} ) B
  where B.num >= #{param2}

choose.when:
    			select p_name
    			from(
    			select rownum as rnum, p_name
				from(
					select distinct p_name
					from product_list
					where p_name like '%${searchword.toUpperCase()}%'
				)
				where rownum < 6
				)

choose.otherwise:
    			select ${searchfield} 
				from(
					select ROWNUM as rnum, ${searchfield}
					from (
					select distinct ${searchfield}
					from product_list
					where ${searchfield} like '%${searchword.toUpperCase()}%'
					)
				)
				where rownum < 6
