mapper.select:
		select count(*) as total from product_list

		 	where ${param2} = #{param3}


          	where ${param2} like '%${param3}%'

select *
  from (
  		select rownum num, A.*
  		  from (
  		        select *
  		          from product_list

  		           	where ${param3} = #{param4}


  		          	where ${param3} like '%${param4}%'

  		         ) A
  		  where rownum <= #{param1} ) B
  where B.num >= #{param2}
