mapper.select:
		select count(*) as total from product_list

		 	where ${sField} = #{sWord}


          	where upper(${sField}) like '%${sWord.toUpperCase()}%'


         	where ${sField} < 500000

          	  and p_company = #{selected}



         	where ${sField} between ${sWord} and ${sWord} + 500000

         	  and p_company = #{selected}



         	where ${sField} between ${sWord} and ${sWord} + 1000000

         	  and p_company = #{selected}



         	where ${sField} >= 5000000

          	  and p_company = #{selected}

select *
  from (
  		select rownum num, A.*
  		  from (
  		        select *
  		          from product_list

  		          	where ${sField} < 500000

  		          	  and p_company = #{selected}

  		          	order by ${sField}


  		          	where ${sField} between ${sWord} and ${sWord} + 500000

		          	  and p_company = #{selected}

			        order by ${sField}


  		          	where ${sField} between ${sWord} and ${sWord} + 1000000

		          	  and p_company = #{selected}

			        order by ${sField}


  		          	where ${sField} >= 5000000

		          	  and p_company = #{selected}

			        order by ${sField}


  		           	where ${sField} = #{sWord}


  		          	where upper(${sField}) like '%${sWord.toUpperCase()}%'

  		         ) A
  		  where rownum <= #{nEnd} ) B
  where B.num >= #{nStart}

select distinct u_id
  from b_orderinfo 
 where p_num = #{param1}
   and u_id = #{param2}

choose.when:
    			select p_name
    			from(
    			select rownum as rnum, p_name
				from(
					select distinct p_name
					from product_list
					where p_name like '%${searchword.toUpperCase()}%'
				)
				where rownum < 6
				)

choose.otherwise:
    			select ${searchfield} 
				from(
					select ROWNUM as rnum, ${searchfield}
					from (
					select distinct ${searchfield}
					from product_list
					where ${searchfield} like '%${searchword.toUpperCase()}%'
					)
				)
				where rownum < 6

mapper.update:
update product_list
   set p_scount = p_scount + 1
 where p_num = #{param1}
